UPDATE `db_script_string` SET content_loc7='¡Venga, vengan todos! ¡Bienvenidos a la Feria de la Luna Negra! ¿Se te antoja una aventura? ¿Buscas tesoros exóticos y misteriosos? ¡Entonces no busques más! Tú, mi $Gamigo:amiga;, ¡has venido al lugar indicado! ¡Sumérgete y toma parte en todo lo que la Feria tiene para ofrecer! Vamos a estar en este lugar toda la semana, ¡así que asegúrense de decirselo a sus amigos y seres queridos!' WHERE entry=2000000198;
UPDATE `db_script_string` SET content_loc7='¡Bienvenidos todos y cada uno a la Feria de la Luna Negra, el más grande evento de todos los mundos! Lo tenemos todo... ¡comida deliciosa, bebidas fuertes, artefactos exóticos, lectura de la fortuna, premios increíbles y emociones sin fin! ¡No olvides canjear tus vales de la Feria de la Luna Negra con Gelvas Roñoso! Todo lo que pide son cinco o más y ya estarás recibiendo los premios más maravillosos de todo Terrallende. ¡Todo el mundo gana!' WHERE entry=2000000199;
UPDATE `db_script_string` SET content_loc7='Mamá, ¿Puedo quedarme con Esfumador?' WHERE entry=2000000200;
UPDATE `db_script_string` SET content_loc7='¡Pato!' WHERE entry=2000000201;
UPDATE `db_script_string` SET content_loc7='¡No vayas cerca del lago! ¡está encantada!' WHERE entry=2000000202;
UPDATE `db_script_string` SET content_loc7='Haré mi mejor esfuerzo para cuidar de los niños, abuela.' WHERE entry=2000000203;
UPDATE `db_script_string` SET content_loc7='¡Bien, encárgate y vamos a ver lo que consigues!' WHERE entry=2000000204;
UPDATE `db_script_string` SET content_loc7='¡El Gran Gazlowe!' WHERE entry=2000000205;
UPDATE `db_script_string` SET content_loc7='Tengo otra lectura del interior.' WHERE entry=2000000206;
UPDATE `db_script_string` SET content_loc7='Vale. Acabamos destrozando nuestras colas para transportar todas estas cosas aquí, ¿y quieres que trabajemos más?' WHERE entry=2000000207;
UPDATE `db_script_string` SET content_loc7='El viento nos habla de los movimientos de nuestros enemigos, Ashyen. Los Naga atacarán nuevamente.' WHERE entry=2000000208;
UPDATE `db_script_string` SET content_loc7='Muy bien. Cuando esa decisión se tome, estaré listo. Esperemos que no sea demasiado tarde.' WHERE entry=2000000209;
UPDATE `db_script_string` SET content_loc7='Estoy buscando en esa recompensa después de este cambio.' WHERE entry=2000000210;
UPDATE `db_script_string` SET content_loc7='¿Quieres ir a pesar que la recompensa de este cartel de buscar se ha acabado después de este cambio?' WHERE entry=2000000211;
UPDATE `db_script_string` SET content_loc7='¡Sé el primero de tu vecindario en poseer el increíble Lanzador de maculación electrostática!' WHERE entry=2000000212;
UPDATE `db_script_string` SET content_loc7='Derivado de la tecnología militar de vanguardia de la Ofensiva Mano Devastada, ¡quita manchas dificiles de sacar!' WHERE entry=2000000213;
UPDATE `db_script_string` SET content_loc7='¡Manchas de vino, manchas de césped, aún manchas de mascotas no son rivales para el Lanzador de maculación electrostática!' WHERE entry=2000000214;
UPDATE `db_script_string` SET content_loc7='¡No espere más! Haga ahora su pedido y recibirá mis papas ultra-cinéticas de hierro sin cargo adicional!' WHERE entry=2000000215;
UPDATE `db_script_string` SET content_loc7='Ya viene, Papasfritas. ¿No tienes una demostración del producto que hacer o algo así?' WHERE entry=2000000216;
UPDATE `db_script_string` SET content_loc7='¡Dinos dónde se esconde tu líder!' WHERE entry=2000000217;
UPDATE `db_script_string` SET content_loc7='¡Por favor! Debo alimentarme de algo pronto... Un cristal de maná... un fragmento... ¡Cualquier cosa! ¡El dolor es insoportable!' WHERE entry=2000000218;
