UPDATE `db_script_string` SET content_loc7='No me responsabilizaré de la catástrofe que seguramente resultará del caótico mantenimiento de este edificio.' WHERE entry=2000000224;
UPDATE `db_script_string` SET content_loc7='¡Esto no servirá! Tenemos que asegurar los cimientos o toda la estructura se desmoronará.' WHERE entry=2000000225;
