UPDATE `db_script_string` SET content_loc7='REZAD MIS SIERVOS' WHERE entry=2000000228;
UPDATE `db_script_string` SET content_loc7='¡Boletos! ¡Canjea tus boletos premiados de la Feira de la Luna Negra aquí! ¡Exóticos y marabillosos premios te esperan!' WHERE entry=2000000229;
UPDATE `db_script_string` SET content_loc7='¡Sin miedo! Se pueden discirnir todas las fortunas para los que conocen el camino.' WHERE entry=2000000230;
UPDATE `db_script_string` SET content_loc7='¡Eh, alguien me ayuda a coger esta cosa!' WHERE entry=2000000231;
UPDATE `db_script_string` SET content_loc7='¿Todo el mundo está disfrutando? ¡Genial! ¡Bienvenidos a la Feria de la Luna Negra, el espectáculo más grande en toda Terrallende! Asegurate aquí de hablar con Yebb y sus aminos mienbras visitas al Tesorillos en el Zoo de los extraños de la Luna Negra.' WHERE entry=2000000232;
UPDATE `db_script_string` SET content_loc7='Saludos amigos, y bienvenidos al mayor espectáculo de Azeroth!$B$BPor favor, pasen y tomen de todo lo que le ofrecemos. ¡Desen un paseo por las atracciones y sitios de interes! Sorpréndase de las maravillas que la Feria de la Luna Negra ha descubierto en este vasto y misterioso mundo! ¡No hemos escatimado en gastos para traer emociones con las que los niños de todas las edades se deleitarán!' WHERE entry=2000000233;
UPDATE `db_script_string` SET content_loc7='Estos datos serán muy útiles para el Proyecto Libro Azul, Quiero decir.., esto será muy bueno para mi nuevo look. A ver si puedes recoger más datos.' WHERE entry=2000000234;
