UPDATE `db_script_string` SET content_loc7='¿Relajarme? ¿Sabes hace Kael\'thas con los que fallan, Geezle? sufrimiento y dolor eterno... ¡Así que ten cuidado, estúpico!.' WHERE entry=2000000292;
UPDATE `db_script_string` SET content_loc7='Nuestros exploradores de Bruma de Sangre han encontrado a nuestro contacto. El estúpido, Velen, pronto se dejará abierto e indefenso - ¡el tiempo suficiente para que ataquemos! Ahora sal de mi vista antes de que te evapore...' WHERE entry=2000000293;
UPDATE `db_script_string` SET content_loc7='Tienes toda mi atención, dragón. Ya verás que no me asusto tan fácilmente como los aldeanos de abajo.' WHERE entry=2000000294;
