UPDATE `db_script_string` SET content_loc7='Tus andanzas en lo arcano han llegado demasiado lejos, Medivh. Has atraído la atención de poderes más allá de tu comprensión. ¡Debes dejar Karazhan de una vez!' WHERE entry=2000000298;
UPDATE `db_script_string` SET content_loc7='¡Pretendes usar un poder oscuro, Medivh! Si te quedas, nos vendrán días terribles. ¡Debes darte prisa, no nos queda mucho tiempo!' WHERE entry=2000000299;
UPDATE `db_script_string` SET content_loc7='No me dejas otra alternativa. ¡Te detendré por la fuerza si te atienes a razones!' WHERE entry=2000000300;
UPDATE `db_script_string` SET content_loc7='¿Qué me has hecho, mago? ¡No puede ser! ¡Me estoy quemando... por dentro!' WHERE entry=2000000301;
UPDATE `db_script_string` SET content_loc7='¡Tenemos un ganador! ¡$N es el Maestro pescador!' WHERE entry=2000000302;
UPDATE `db_script_string` SET content_loc7='Espécimen metálico de extraño origen detectado.' WHERE entry=2000000303;
UPDATE `db_script_string` SET content_loc7='Comenzando el análisis...' WHERE entry=2000000304;
UPDATE `db_script_string` SET content_loc7='Muestra identificada como una aleación de saronita y hierro.' WHERE entry=2000000305;
UPDATE `db_script_string` SET content_loc7='El origen del mineral usado en esta muestra está en las profundidades de la corteza de Azeroth y fue descubierto recientemente.' WHERE entry=2000000306;
UPDATE `db_script_string` SET content_loc7='La saronita de esta muestra es casi pura. Un mineral como este no comun en la superficie de Azeroth.' WHERE entry=2000000307;
UPDATE `db_script_string` SET content_loc7='Este mineral sólo puede haber sido creado por una poderosa y maligna fuerza. Intentaré acceder a las bases de datos para obtener más información...' WHERE entry=2000000308;
UPDATE `db_script_string` SET content_loc7='¡¿Acceso denegado?!' WHERE entry=2000000309;
UPDATE `db_script_string` SET content_loc7='Información adicional únicamente se puede obtener en los archivos internos de Ulduar, por orden del Guardián Loken.' WHERE entry=2000000310;
UPDATE `db_script_string` SET content_loc7='Pero una cosa es cierta: el tamaño de esta placa, el espesor, y los agujeros de los tornillos apuntan a un uso como armadura de una estructura colosal.' WHERE entry=2000000311;
UPDATE `db_script_string` SET content_loc7='Orden completada. Volviendo al modo de espera.' WHERE entry=2000000312;
UPDATE `db_script_string` SET content_loc7='¡Yo... ahora te entiendo! ¡Bien, ahora que podemos hablar, tienes que darme algunas explicaciones!' WHERE entry=2000000313;
UPDATE `db_script_string` SET content_loc7='¿Cómo consegiste mi comunicador de la Liga de Expedicionarios?' WHERE entry=2000000314;
UPDATE `db_script_string` SET content_loc7='Tienes mi agradecimiento por despachar los enanos férreos. Pero ¿por qué la Horda tiene este interés en mí?' WHERE entry=2000000315;
UPDATE `db_script_string` SET content_loc7='Esto no será cosa del Explorador Vor\'takh, ¿verdad? conozco su fama de cruel. Entonces, ¿quiere decir que me secuestran?' WHERE entry=2000000316;
UPDATE `db_script_string` SET content_loc7='Si has visto el diario, entonces sabes lo que he ido descubriendo. ¡Las mismas creaciones de los Titanes están en guerra unas con otras!' WHERE entry=2000000317;
UPDATE `db_script_string` SET content_loc7='Loken y sus secuaces enanos de hierro han expulsado a los terráneos de Ulduar y se han apoderado de la ciudad.' WHERE entry=2000000318;
UPDATE `db_script_string` SET content_loc7='Si gastamos nuestro tiempo y fuerzas en pelearnos unos con otros, Loken utilizará los recursos de Ulduar para destruir tanto a la Horda como a la Alianza.' WHERE entry=2000000319;
UPDATE `db_script_string` SET content_loc7='Habla con el comandante en tu puesto, $g;muchacho:muchacha, y persuádelo a abandonar el insensato plan de Vor\'takh.' WHERE entry=2000000320;
UPDATE `db_script_string` SET content_loc7='Si la Liga de Expedicionarios envió hombres, no he visto ni rastro de ellos. Encontré tu nota enterrada en un campo de invadido por enanos férreos.' WHERE entry=2000000321;
UPDATE `db_script_string` SET content_loc7='Un explorador encontró los restos de tu diario en Thor Modan. Te hemos estado buscando desde entonces.' WHERE entry=2000000322;
