UPDATE `db_script_string` SET content_loc7='El bazar debe hacer buenos negocios. ¡Voy a reabastecerle de inmediato!' WHERE entry=2000000326;
UPDATE `db_script_string` SET content_loc7='Hm ... después de zanjar esto, creo que iré a la tienda de quesos a por la merienda.' WHERE entry=2000000327;
UPDATE `db_script_string` SET content_loc7='Debería sobrarme dinero de esta venta. Tal vez me voy a comprar algo de comer...' WHERE entry=2000000328;
UPDATE `db_script_string` SET content_loc7='Hey Harlan. Aquí hay una carga de géneros tricotados para ti.' WHERE entry=2000000329;
UPDATE `db_script_string` SET content_loc7='¡Uoph! Aquí está carga de suministros. ¡Se debe atender rápido!' WHERE entry=2000000330;
