UPDATE `db_script_string` SET content_loc7='[Furbolg] ¡Los furbolgs Semprepino no olvidaremos tu valentía!' WHERE entry=2000000496;
UPDATE `db_script_string` SET content_loc7='¿Qué es esto?' WHERE entry=2000000497;
UPDATE `db_script_string` SET content_loc7='¿El... lo guardaba? ¿Durante todo este tiempo, se lo guardó!' WHERE entry=2000000498;
UPDATE `db_script_string` SET content_loc7='¡Lo sabía!' WHERE entry=2000000499;
UPDATE `db_script_string` SET content_loc7='¡Sentía que una parte de él aún vivía! ¡Atrapado... luchando... Oh, Arthas!' WHERE entry=2000000500;
UPDATE `db_script_string` SET content_loc7='Tal vez - tal vez algún día pueda recordar lo que un vez fue.' WHERE entry=2000000501;
UPDATE `db_script_string` SET content_loc7='Por la luz, es posible que por fin encuentre descanso, libre de garra helada de esa terrible espada.' WHERE entry=2000000502;
UPDATE `db_script_string` SET content_loc7='Así que, se hace.' WHERE entry=2000000503;
UPDATE `db_script_string` SET content_loc7='No me hubiese atrevido en confiar mis sentidos - tubo varias ocasiones el Rey Exánime para hacerme un necio.' WHERE entry=2000000504;
UPDATE `db_script_string` SET content_loc7='Finalmente, se le ha hecho pagar por las atrocidades que le impuso a la gente.' WHERE entry=2000000505;
UPDATE `db_script_string` SET content_loc7='Azeroth nunca debe olvidar el terrible precio que pagamos por nuestra flaqueza... por nuestro orgullo.' WHERE entry=2000000506;
UPDATE `db_script_string` SET content_loc7='¿Pero ahora qué, $ghéroe:heronina? ¿Qué será de los liberados de sus garras, pero aún encadenados a sus cuerpos mortales?' WHERE entry=2000000507;
UPDATE `db_script_string` SET content_loc7='Déjame.' WHERE entry=2000000508;
UPDATE `db_script_string` SET content_loc7='Tengo mucho en que pensar.' WHERE entry=2000000509;
UPDATE `db_script_string` SET content_loc7='Arthas...' WHERE entry=2000000510;
UPDATE `db_script_string` SET content_loc7='Ah, $ghéroe:heronina; de Azeroth, me dás un regalo más grande de lo que crees.' WHERE entry=2000000511;
UPDATE `db_script_string` SET content_loc7='Largo tiempo he luchado para perdonar al principe por sus terribles transgresiones.' WHERE entry=2000000512;
UPDATE `db_script_string` SET content_loc7='Mi alma ha sido azotada con insoportable angustia, pensamientos oscuros... distanciandome de la Luz.' WHERE entry=2000000513;
UPDATE `db_script_string` SET content_loc7='Me acuerdo claramente de su soberbia y el resplandor de sus ojos, mientras permanecía de pie frente a mí, dispuesto a derrotar a los enemigos de la Luz…' WHERE entry=2000000514;
UPDATE `db_script_string` SET content_loc7='Ansioso por defender a su gente, sin importar lo que cueste.' WHERE entry=2000000515;
UPDATE `db_script_string` SET content_loc7='Esa es la memoria de Arthas que decido guardar en mi corazón.' WHERE entry=2000000516;
UPDATE `db_script_string` SET content_loc7='Estaré siempre en deuda contigo, amig$go:a;.' WHERE entry=2000000517;
UPDATE `db_script_string` SET content_loc7='Gracias.' WHERE entry=2000000518;
UPDATE `db_script_string` SET content_loc7='Oh, muchach$go:a;...' WHERE entry=2000000519;
UPDATE `db_script_string` SET content_loc7='Cómo añoro esos interminables días en Lordaeron...' WHERE entry=2000000520;
UPDATE `db_script_string` SET content_loc7='…afinando tu\' habilidade\'h con la hoja mate...' WHERE entry=2000000521;
UPDATE `db_script_string` SET content_loc7='…fo\'jandote un arma de\'htinada a z\'oporta\' la\'h demanda\'h de un gran destino.' WHERE entry=2000000522;
UPDATE `db_script_string` SET content_loc7='¿Heh... Sí seguro que la pone\'h a prueba, no es así, muchach$go:a;?' WHERE entry=2000000523;
UPDATE `db_script_string` SET content_loc7='Si te hubiese para\'o ese día, la\'h cosa\'h hubisen sido diferente\'.' WHERE entry=2000000524;
UPDATE `db_script_string` SET content_loc7='Si nunca hubiese descubierto esa maldita hoja' WHERE entry=2000000525;
UPDATE `db_script_string` SET content_loc7='Adiós, Arthas... hermano mío.' WHERE entry=2000000526;
UPDATE `db_script_string` SET content_loc7='Darion, hijo mío.' WHERE entry=2000000527;
UPDATE `db_script_string` SET content_loc7='Al fin, puedo poner mis ojos en ti otra vez.' WHERE entry=2000000528;
UPDATE `db_script_string` SET content_loc7='El Rey Exánime me atormentaba sin fin, Darion.' WHERE entry=2000000529;
UPDATE `db_script_string` SET content_loc7='Sin cesar, trató de romper mi voluntad, para obligarme a servirle, para unirme a su espada...' WHERE entry=2000000530;
UPDATE `db_script_string` SET content_loc7='Finalmente, cuando los acontecimientos exigieron toda su atención, me dejó.' WHERE entry=2000000531;
UPDATE `db_script_string` SET content_loc7='Me aferré al único recuerdo, Darion... El único pensamiento que me impidió dar…' WHERE entry=2000000532;
UPDATE `db_script_string` SET content_loc7='Fue tu sacrificio, hijo mío, el que de nuevo me salvó de un peligro eterno.' WHERE entry=2000000533;
UPDATE `db_script_string` SET content_loc7='Padre…' WHERE entry=2000000534;
UPDATE `db_script_string` SET content_loc7='Padre. Temí por... tu cordura.' WHERE entry=2000000535;
UPDATE `db_script_string` SET content_loc7='Padre... por tí, daría mi vida mil veces.' WHERE entry=2000000536;
UPDATE `db_script_string` SET content_loc7='¡%s huele el elixir entonces con impaciencia entierra!' WHERE entry=2000000537;
UPDATE `db_script_string` SET content_loc7='Y gracias, $N. Sin tu ayuda nunca hubiese encontrado mi camino a los Renegados.' WHERE entry=2000000538;
UPDATE `db_script_string` SET content_loc7='Adiós, mi amigo.' WHERE entry=2000000539;
UPDATE `db_script_string` SET content_loc7='Durante mucho tiempo, has sido mi amigo, Bethor.' WHERE entry=2000000540;
UPDATE `db_script_string` SET content_loc7='Cuando el tiempo lo permita, tenemos que hablar largo y tendido. Ya que tenemos mucho de lo que discutir.' WHERE entry=2000000541;
UPDATE `db_script_string` SET content_loc7='%s invoca a un mensajero…' WHERE entry=2000000542;
UPDATE `db_script_string` SET content_loc7='Vete, siervo. Envia el recapo a la boticaria Zamah del Espolón.' WHERE entry=2000000543;
UPDATE `db_script_string` SET content_loc7='%s da el mensaje a la boticaria Zamah.' WHERE entry=2000000544;
UPDATE `db_script_string` SET content_loc7='La boticaria Zamah ríe.' WHERE entry=2000000545;
UPDATE `db_script_string` SET content_loc7='¡Pero este es la única costrucción en el que Brogg entra! ¡Los Goblins hacen costrucciones muy pequeñas!' WHERE entry=2000000546;
UPDATE `db_script_string` SET content_loc7='Creí que ya hablamos de esto, Brogg. No puedes quedarte en esta costrucción. ¡Seguro, podria ser tu casa, pero no hay espacio para nadie más!' WHERE entry=2000000547;
UPDATE `db_script_string` SET content_loc7='Sí, digimos que podias quedarte aquí, ¡pero este edificio es nuestro puesto de mando! ¡Todos nuestros manuales y planos se almacenan aquí!' WHERE entry=2000000548;
UPDATE `db_script_string` SET content_loc7='¡Me inclino a decir que hicieron los ogros demasiado grandes! ¡Veo que ahora, no te voy a convercer de que te muevas, pero dudo que esto dure mucho!' WHERE entry=2000000549;
UPDATE `db_script_string` SET content_loc7='¡Tú has dicho a Brogg que podía quedarme aquí! ¡Prometiste a Brogg que le ayudarías a recuperar el montículo del clan Quebrantarrocas!' WHERE entry=2000000550;
UPDATE `db_script_string` SET content_loc7='En la convulsión del Hendimiento, Ameth\'Aran estaba a merced del terror que se apoderó de la tierra. Había pocas esperanzas de supervivencia.' WHERE entry=2000000551;
UPDATE `db_script_string` SET content_loc7='Athrikus llegó a nosotros. Nos dijo que nos podría salvar de cualquier peligro. Lanzó un hechizo sobre nosotros para que nos protegiese de cualquier daño.' WHERE entry=2000000552;
UPDATE `db_script_string` SET content_loc7='Cuando el temblor se detuvo, sus verdaderos motivos fueron revelados. Estábamos atrapados, y fue agotando lentamente nuestros poderes.' WHERE entry=2000000553;
UPDATE `db_script_string` SET content_loc7='Había cientos apresados por su hechizo. Ahora sólo unos pocos permanecen en sus cárceles. A veces nos hablaba.' WHERE entry=2000000554;
UPDATE `db_script_string` SET content_loc7='Le preocupaba que su poder se debilitara, que pronto, nuestra última de sus preciadas Gemas de alma se desvanezca y muriera.' WHERE entry=2000000555;
UPDATE `db_script_string` SET content_loc7='Su teniente, Ilkurd Magthrull posee un tomo que podría indicar la localización de las gemas de alma restantes.' WHERE entry=2000000556;
UPDATE `db_script_string` SET content_loc7='Ummm ... ¿Dónde está mi anzuelo? Oh, está ahí.' WHERE entry=2000000557;
UPDATE `db_script_string` SET content_loc7='Vamos a ver si los peces pican.' WHERE entry=2000000558;
UPDATE `db_script_string` SET content_loc7='Ummm... Creo que necesito un cebo mejor. Voy a mirar en el carro de mi padre.' WHERE entry=2000000559;
UPDATE `db_script_string` SET content_loc7='¡Trozos de Jabaespines! Estos deberían hacer el engaño. El pescado ama los Jabaespines.' WHERE entry=2000000560;
UPDATE `db_script_string` SET content_loc7='¡Pillé uno! Voy a ver si alguno de los guardias está hambriento. ¡Vamos colmillos!' WHERE entry=2000000561;
UPDATE `db_script_string` SET content_loc7='Pillé un pez, si tienes hambre...' WHERE entry=2000000562;
UPDATE `db_script_string` SET content_loc7='¡Eso es una buena captura! ¡Honras a la Horda chico, te lo agradecemos!' WHERE entry=2000000563;
UPDATE `db_script_string` SET content_loc7='¿Quiere algo más de comida señor?' WHERE entry=2000000564;
UPDATE `db_script_string` SET content_loc7='¡Más pan chico!' WHERE entry=2000000565;
UPDATE `db_script_string` SET content_loc7='En seguida señor.' WHERE entry=2000000566;
UPDATE `db_script_string` SET content_loc7='Aquí está su pan señor.' WHERE entry=2000000567;
UPDATE `db_script_string` SET content_loc7='Aaahh... Muy bien. ¡Ahora largo!' WHERE entry=2000000568;
UPDATE `db_script_string` SET content_loc7='Como desee, señor.' WHERE entry=2000000569;
UPDATE `db_script_string` SET content_loc7='¿Puedo ir a recoger más piedras para mi collar, papá?' WHERE entry=2000000570;
UPDATE `db_script_string` SET content_loc7='Si puedes, $N, pero no te alejes demasiado de casa.' WHERE entry=2000000571;
UPDATE `db_script_string` SET content_loc7='¡Dabu! ¡Tendré cuidadoPapa!' WHERE entry=2000000572;
UPDATE `db_script_string` SET content_loc7='Ooohh… Esta roca es bonita y colorida. Voy a ver si puedo encontrar más como esta…' WHERE entry=2000000573;
UPDATE `db_script_string` SET content_loc7='Umm... Esa roca bonita, pero muy pequeña.' WHERE entry=2000000574;
UPDATE `db_script_string` SET content_loc7='Esta es una roca brillante. Lo pondré en mi cubo. Ok, sólo necesito algunas más…' WHERE entry=2000000575;
UPDATE `db_script_string` SET content_loc7='¡Apuesto que a mi amigo Gruk le gustaría esta roca para la lanza que está haciendo! Está muy afilada... Espero que le guste.' WHERE entry=2000000576;
UPDATE `db_script_string` SET content_loc7='Allí... debe haber bastantes rocas como para hacer un collar. Quizás debería volver a casa de todos modos.' WHERE entry=2000000577;
UPDATE `db_script_string` SET content_loc7='¡Vas a pagar por esta afrenta, $R!' WHERE entry=2000000578;
UPDATE `db_script_string` SET content_loc7='¡Tu cuerpo es mío! ¡Nuestros destinos están unidos para siempre!' WHERE entry=2000000579;
UPDATE `db_script_string` SET content_loc7='La Cruzada Argenta te invita a probar tus habilidades en honorable combate contra  otros héroes de Azeroth en el Torneo Argenta.' WHERE entry=2000000580;
UPDATE `db_script_string` SET content_loc7='Los campeones de las grandes naciones del mundo se han reunido en los Campos del Torneo Argenta en Corona de Hielo Rasganorte para que se enfrenten todos los aspirantes.' WHERE entry=2000000581;
UPDATE `db_script_string` SET content_loc7='golpea las retorcidas garras.' WHERE entry=2000000999;
UPDATE `db_script_string` SET content_loc7='Yo había planeado una cumbre secreta entre el Jefe de Guerra Thrall y el Rey Wyrn, aquí en Theramore.' WHERE entry=2000001000;
UPDATE `db_script_string` SET content_loc7='El rey viajaba hasta aquí en barco, en secreto. Pero el barco nunca llegó, y el Rey Wrynn desapareció.' WHERE entry=2000001001;
UPDATE `db_script_string` SET content_loc7='Su ayuda nos permitió conectar los Defias al cerebro que hay tras el secuestro, Onyxia, que se hacía pasar como miembro de la corte real.' WHERE entry=2000001002;
UPDATE `db_script_string` SET content_loc7='Mientras tanto, el rey logró escapar de sus captores, y volvía a Ventormenta para hacer frente a Onyxia.' WHERE entry=2000001003;
UPDATE `db_script_string` SET content_loc7='Enviaré recado de lo que has descubierto del enlace de los Defias. Fui un idiota al créeles roto y derrotado a Edwin Vancleef, y el Rey Wrynn quiere escarmentar a todos los que se queden.' WHERE entry=2000001004;
UPDATE `db_script_string` SET content_loc7='Entre los cráneos ningún Yenniku, $N. Eso es excelente para mi alívio.' WHERE entry=2000001005;
UPDATE `db_script_string` SET content_loc7='¿Que fuerzas me atraviesan? ¿Quizá sea el poder chamánico, del que habló Tor\'gan? ..' WHERE entry=2000001006;
UPDATE `db_script_string` SET content_loc7='¡Increíble! Me siento renacer. ¡Obligado a volver!' WHERE entry=2000001007;
UPDATE `db_script_string` SET content_loc7='Gracias, $N.' WHERE entry=2000001008;
UPDATE `db_script_string` SET content_loc7='Yo, por supuesto, encontré aplicaciones de la mezcla...' WHERE entry=2000001009;
UPDATE `db_script_string` SET content_loc7='¡Ja! $N, ¡No se muevan de sus asientos!' WHERE entry=2000001010;
UPDATE `db_script_string` SET content_loc7='Intenta abrir la pata de palo de Maury...' WHERE entry=2000001011;
UPDATE `db_script_string` SET content_loc7='Mostrando El Ojo de la mente y Cristales de canto...' WHERE entry=2000001012;
UPDATE `db_script_string` SET content_loc7='Gemas de Alma Yenniku listas, $N...' WHERE entry=2000001013;
UPDATE `db_script_string` SET content_loc7='Azul con rojo... Lo que me temía...' WHERE entry=2000001015;
UPDATE `db_script_string` SET content_loc7='¡Aquí está, $N! ¡Al final, lo entendí!' WHERE entry=2000001016;
UPDATE `db_script_string` SET content_loc7='Mira nervioso.' WHERE entry=2000001017;
UPDATE `db_script_string` SET content_loc7='¿Estás seguro que no ha seguido al nuestro? Tengo un mal presentimiento...' WHERE entry=2000001018;
UPDATE `db_script_string` SET content_loc7='¡Aguarda!' WHERE entry=2000001019;
UPDATE `db_script_string` SET content_loc7='¡Jamás se me hubiese ocurrido pensar que este aceite fuera maná! Oh, eso es todo lo que necesitas, a exepción de Grek, por supuesto.' WHERE entry=2000001020;
UPDATE `db_script_string` SET content_loc7='¡Vete!' WHERE entry=2000001021;
UPDATE `db_script_string` SET content_loc7='No, así no. ¿Qué pasó aquí?' WHERE entry=2000001022;
UPDATE `db_script_string` SET content_loc7='El metal perdió su mágia. Pude ver su arma, pero no diferirá de la hoja convencional.' WHERE entry=2000001023;
UPDATE `db_script_string` SET content_loc7='La información que estás buscando... No me es tan simple hacerla. Demuestre que tenemos enemigos comunes.' WHERE entry=2000001024;
UPDATE `db_script_string` SET content_loc7='Recuerda, $N, el polvo de Sanguiespora se puede usar para debilitar a todos magnatauros en Gammothra.' WHERE entry=2000001025;
UPDATE `db_script_string` SET content_loc7='Asegúrete de entrar en el santuario y salir por el paso sur, $N. Ya sabes pasa donde está la batalla para protegerlos de la abominación... mantenlos alejados.' WHERE entry=2000001030;
UPDATE `db_script_string` SET content_loc7='chamuscar los troolls de la plaga...' WHERE entry=2000001031;
UPDATE `db_script_string` SET content_loc7='...en una hoguera...' WHERE entry=2000001032;
UPDATE `db_script_string` SET content_loc7='eructa con fuerza...' WHERE entry=2000001033;
UPDATE `db_script_string` SET content_loc7='¡Eso es todo! Hasta donde yo se ... ¿Qué te parece?' WHERE entry=2000001034;
UPDATE `db_script_string` SET content_loc7='Dioses del pasado, os apelamos. ¿Qué hacemos? ¿Cómo podemos salvar a los dioses de la tierra?' WHERE entry=2000001035;
UPDATE `db_script_string` SET content_loc7='¡Excelentes cuernos! Resultaría difícil elegir cual es mejor...' WHERE entry=2000001036;
UPDATE `db_script_string` SET content_loc7='Quizá elija este cuerno. ¡Ahora sólo tienes que cortarlo correctamente! Tal vez no sea fácil. ¡Porque ni siquiera yo sabría cómo sujetar a la hembra yeti!' WHERE entry=2000001037;
UPDATE `db_script_string` SET content_loc7='Corté el cuerno, pero antes de poner en él la talla artesanal, me aseguraré de que funcione. ¿Me ayudarás a probarlo?' WHERE entry=2000001038;
UPDATE `db_script_string` SET content_loc7='¡Excelente $N! ¡Esto es algo que te deberemos! Y ahora - al trabajo.' WHERE entry=2000001039;
UPDATE `db_script_string` SET content_loc7='¡Es...suficiente por hoy!' WHERE entry=2000001040;
UPDATE `db_script_string` SET content_loc7='Recuerda, $N, este no es el carrera. Esta verificación de los cazadores, es una prueba de resistencia de una larga expedición.' WHERE entry=2000001041;
UPDATE `db_script_string` SET content_loc7='Te equivocas en todo, $N. En las historias que la Domadora del viento Barah me contó, Pezuña Tempestuosa era un héroe, no de los villanos.' WHERE entry=2000001042;
UPDATE `db_script_string` SET content_loc7='Viento del Norte – el antiguo enemigo taunka, maestro de los elementos y la misma fuerza que priva la vida de este antiguo pueblo.' WHERE entry=2000001043;
UPDATE `db_script_string` SET content_loc7='Preferirán morir antes que inclinarse ante Viento del norte. Hay algo mal.' WHERE entry=2000001044;
UPDATE `db_script_string` SET content_loc7='Martillo del conocimiento...' WHERE entry=2000001045;
UPDATE `db_script_string` SET content_loc7='Esto confirma nuestras sospechas. La línea de tiempo está dañada y tejiendo en el patrón incorrecto. Pero, ¿cómo?' WHERE entry=2000001046;
UPDATE `db_script_string` SET content_loc7='¡Oh, son pegajosas! ¿Me pregunto cómo se disparan las correas?' WHERE entry=2000001047;
UPDATE `db_script_string` SET content_loc7='¡ACK! ¡Oh, no, me enredé! ¡Es pegajosa. MI PELO!' WHERE entry=2000001048;
UPDATE `db_script_string` SET content_loc7='No te preocupes, amiga. Rokhan se encargará de todo. No hay escapatoria por ninguna parte.' WHERE entry=2000001049;
UPDATE `db_script_string` SET content_loc7='¡Espíritus escucharnos! ¿Los sientes, $N?' WHERE entry=2000001050;
UPDATE `db_script_string` SET content_loc7='El bosque se encuentra en el lado oeste de la Conquista del Invierno. Busca la sombra del corazón contráctil. Sin duda que se alegrarán de tu llegada...' WHERE entry=2000001051;
UPDATE `db_script_string` SET content_loc7='¡Mátalos a todos, $N!' WHERE entry=2000001052;
UPDATE `db_script_string` SET content_loc7='Y ahora voy a llenar este luminoso cristal, de manera que descartaremos todas las mentiras y el engaños.' WHERE entry=2000001053;
UPDATE `db_script_string` SET content_loc7='Sepamos la verdad. Si entre nosotros se esconde un asesino, y señala la dirección correcta para buscarlo.' WHERE entry=2000001054;
UPDATE `db_script_string` SET content_loc7='Escucha el informe de $N...' WHERE entry=2000001055;
UPDATE `db_script_string` SET content_loc7='¿Qué? ¿Hoja de fuego amplía el área de influencia? ¡Tenemos que explorar lo que sea!' WHERE entry=2000001056;
UPDATE `db_script_string` SET content_loc7='Al este de las Cascadas Glaciales, hermano. Trae aquí la mejor armadura que encuentres. A la Alianza no le interesa quienes de ellos atacaron.' WHERE entry=2000001057;
UPDATE `db_script_string` SET content_loc7='Escucha, caballero de la muerte... ¡Debes atender a la llamada de tu amo! ¡Él te está llamando!' WHERE entry=2000001058;
UPDATE `db_script_string` SET content_loc7='¡Guerreros de la Plaga, escuchad mi llamada! ¡Hoy daremos fin a la Orden escarlata! ¡Vaciar las tierras! ¡Afincad aquí permanentemente la muerte!' WHERE entry=2000001059;
UPDATE `db_script_string` SET content_loc7='Parad...' WHERE entry=2000001060;
UPDATE `db_script_string` SET content_loc7='Es sólo que...' WHERE entry=2000001061;
UPDATE `db_script_string` SET content_loc7='¡Cerveza! ¡La cerveza siempre!' WHERE entry=2000001062;
UPDATE `db_script_string` SET content_loc7='No hay nada raro.' WHERE entry=2000001063;
UPDATE `db_script_string` SET content_loc7='¡Hey, tú! ¡Solo trata de derramar los frijoles sobre Grick! ¿Me oyes?' WHERE entry=2000001064;
UPDATE `db_script_string` SET content_loc7='Guardan las herramientas aquí, en estas cajas. Busca en las que hay cerca de las tiendas.' WHERE entry=2000001065;
UPDATE `db_script_string` SET content_loc7='Sólo recorté esas runas, te ayudará a controlar el gigante.' WHERE entry=2000001066;
UPDATE `db_script_string` SET content_loc7='No voy a cortar las runas \"norte\" y \"oeste\" quien sabe el impacto que pueden tener en los gigantes. Aquí... sólo quedaba incluir la lana, lo determinará en cuanto que tome el control de esa criatura.' WHERE entry=2000001067;
UPDATE `db_script_string` SET content_loc7='Espero haberlo cortado bien. Ella, por supuesto, no es tan mortal como las runas de los enanos, pero también puede que algo lo sea.' WHERE entry=2000001068;
UPDATE `db_script_string` SET content_loc7='Por el amor de Dios, ¡mató a Joe el cojo y se llevó la carne! ¿Cómo sabes dónde encontrarnos?' WHERE entry=2000001069;
UPDATE `db_script_string` SET content_loc7='Bueno, si has venido a matarme, ¿al menos podrías esperar hasta que alimente a Griselda? Pobrecita no ha comido durante varios días.' WHERE entry=2000001070;
UPDATE `db_script_string` SET content_loc7='Aquí, niña. ¡Puedes durarle su última comida antes de que matemos al asesino Joe el cojo!' WHERE entry=2000001071;
UPDATE `db_script_string` SET content_loc7='¿Cómo has podido matar a la pobre Joe? ¿Y por qué traes de vuelta la carne?' WHERE entry=2000001072;
UPDATE `db_script_string` SET content_loc7='Que - una adivinanza, sí, un verdadero misterio... Aquí está... no soy muy aficionado a las adivinanzas.' WHERE entry=2000001073;
UPDATE `db_script_string` SET content_loc7='Será mejor que te vayas, mientras Griselda no te quiera de postre...' WHERE entry=2000001074;
UPDATE `db_script_string` SET content_loc7='La introducción de las runas \"Omega\" se ha completado. La activación fue exitosa.' WHERE entry=2000001075;
UPDATE `db_script_string` SET content_loc7='Fezzix, pienso, decidados tu pequeño problema con el motor.' WHERE entry=2000001076;
UPDATE `db_script_string` SET content_loc7='¡Esta... oscura... energía! ¿Cómo es posible esto?' WHERE entry=2000001077;
UPDATE `db_script_string` SET content_loc7='Este libro me daría poder ilimitado sobre mis enemigos... ¡Soy... irresistible!' WHERE entry=2000001078;
UPDATE `db_script_string` SET content_loc7='¡No, toma esto, $N! Mi voluntad está tan debilitada debido a su poder oscuro. ¡Tómalo, y acaba con Varedis!' WHERE entry=2000001079;
UPDATE `db_script_string` SET content_loc7='He construido unos paneles de control abajo de la cuesta. ¡Rápido toma uno prestado y toma el control del atracador vil! Tienes poco de tiempo.' WHERE entry=2000001080;
UPDATE `db_script_string` SET content_loc7='Más fácil de lo que esperaba... sigilo de Thoradin intacto.' WHERE entry=2000001081;
UPDATE `db_script_string` SET content_loc7='Empieza a leer el hechizo sobre el corazón y las lenguas.' WHERE entry=2000001082;
